mapper.select:
SELECT b.board_id, b.board_title, u.nickname, b.board_created_at FROM board b JOIN users u ON b.user_id = u.user_id WHERE b.is_board_deleted = 'N'
AND b.board_category = #{categoryId}
AND b.board_title LIKE CONCAT('%', #{keyWord}, '%')
AND u.nickname LIKE CONCAT('%', #{nickname}, '%')
AND DATE(b.board_created_at) >= #{startDate}
AND DATE(b.board_created_at) <= #{endDate}
ORDER BY b.board_id DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM board b
JOIN users u ON b.user_id = u.user_id
WHERE b.is_board_deleted = 'N'


    AND b.board_category = #{categoryId}



    AND b.board_title LIKE CONCAT('%', #{keyWord}, '%')



    AND u.nickname LIKE CONCAT('%', #{nickname}, '%')



    AND DATE(b.board_created_at) >= #{startDate}



    AND DATE(b.board_created_at) <= #{endDate}
